namespace aia_api.Application.Replicate;

/*
 * The webhook filter is a list of strings that will be used to filter the webhook
 * May contain the following values:
 * - "start": immediately on prediction start
 * - "output": each time a prediction generates an output (note that predictions can generate multiple outputs)
 * - "logs": each time log output is generated by a prediction
 * - "completed": when the prediction reaches a terminal state (succeeded/canceled/failed)
 */
public record Prediction(string ModelVersion, PredictionInput PredictionInput, string WebhookUrl,
 string[] WebhookFilter);
